import React, { useState } from 'react';
import Chat from './components/Chat';
import './styles/App.scss';
import { IconArrowRight } from '@tabler/icons-react';

function App() {
  const [mode, setMode] = useState('A');
  const [processingSeconds, setProcessingSeconds] = useState(null);
  const [chatKey, setChatKey] = useState(0);
  return <div className='main'>
    <div className='text-description'>
      <div>
        <h1>Chat response</h1>
        <h2>Feature para melhoria de percepção do usuário sobre tempo e racional do AI</h2>
        <div className='point'><IconArrowRight /><p>Modo atual: {mode === 'A' ? 'Modo A' : 'Modo B'}</p></div>
        <div className='point'><IconArrowRight /><p>Tempo de resposta:{processingSeconds ? ` • ~${processingSeconds}s` : ''}</p></div>
        <div>
          <button onClick={() => {
            setMode(m => {
              const next = m === 'A' ? 'B' : 'A';
              if (next === 'B') {
                setProcessingSeconds(null);
                setChatKey(k => k + 1);
              }
              return next;
            });
          }}>Alternar para {mode === 'A' ? 'Modo B' : 'Modo A'}</button>
        </div>

      </div>
    </div>
    <div className='content'><Chat key={chatKey} mode={mode} onProcessingStart={() => {}} onProcessingEnd={(secs) => setProcessingSeconds(secs)} /></div>
  </div>;
}

export default App;
