import React from 'react';
import './MessageLog.scss';
import { IconSparkles } from '@tabler/icons-react';
import { marked } from 'marked';
import { ThreeDots } from 'react-loader-spinner';

export const UserMessage = ({ text }) => (
  <div className="user-message">
    <span>{text}</span>
  </div>
);

export const AiMessage = ({ text, typewriter, onTypewriterEnd }) => {
  const [displayed, setDisplayed] = React.useState(typewriter ? '' : text);
  React.useEffect(() => {
    if (typewriter) {
      setDisplayed('');
      let i = 0;
      const interval = setInterval(() => {
        setDisplayed(text.slice(0, i + 1));
        i++;
        if (i === text.length) {
          clearInterval(interval);
          if (onTypewriterEnd) onTypewriterEnd();
        }
      }, 25);
      return () => clearInterval(interval);
    }
  }, [text, typewriter, onTypewriterEnd]);

  return (
    <div className={`ai-message ${typewriter ? 'ai-typing' : 'ai-completed'}`}>
      <span className="chat-ai-icon"><IconSparkles size={20} stroke={2} /></span>
      <p
        className="ai-message-markdown"
        dangerouslySetInnerHTML={{ __html: marked.parse(typewriter ? displayed : text) }}
      />
      {typewriter && displayed.length < text.length && (
        <span className="ai-typing-dots">
          <span>.</span><span>.</span><span>.</span>
        </span>
      )}
    </div>
  );
};

export default function MessageLog({ messages, aiTyping, showTypewriter, pendingAiMsg, onTypewriterEnd, messagesEndRef, processingSeconds = null, mode = 'A' }) {
  const [stepIdx, setStepIdx] = React.useState(0);
  const [stepTextDisplayed, setStepTextDisplayed] = React.useState('');
  const stepIntervalRef = React.useRef();
  const typingIntervalRef = React.useRef();

  // Estados específicos do Modo B
  const [stepTexts, setStepTexts] = React.useState([]);
  const typingIntervalRefs = React.useRef([]);

  const steps = React.useMemo(() => ([
    'Buscando base de dados (1/4)',
    'Identificando os pontos geográficos (2/4)',
    'Aplicando os filtros definidos (3/4)',
    'Aplicando camada no mapa (4/4)'
  ]), []);
  const stepsNoCounters = React.useMemo(() => ([
    'Buscando base de dados',
    'Identificando os pontos geográficos',
    'Aplicando os filtros definidos',
    'Aplicando camada no mapa'
  ]), []);

  // Modo A - comportamento original
  React.useEffect(() => {
    if (mode !== 'A' || !aiTyping || showTypewriter || !processingSeconds) {
      clearInterval(stepIntervalRef.current);
      clearInterval(typingIntervalRef.current);
      setStepIdx(0);
      setStepTextDisplayed('');
      return;
    }

    const totalMs = processingSeconds * 1000;
    const perStepMs = Math.max(200, Math.floor(totalMs / steps.length));

    const startTypingCurrent = () => {
      clearInterval(typingIntervalRef.current);
      setStepTextDisplayed('');
      const full = steps[stepIdx];
      let i = 0;
      typingIntervalRef.current = setInterval(() => {
        i += 1;
        setStepTextDisplayed(full.slice(0, i));
        if (i >= full.length) {
          clearInterval(typingIntervalRef.current);
        }
      }, 20);
    };

    startTypingCurrent();

    clearInterval(stepIntervalRef.current);
    stepIntervalRef.current = setInterval(() => {
      setStepIdx(prev => {
        const next = prev + 1;
        if (next < steps.length) {
          setStepTextDisplayed('');
          setTimeout(() => startTypingCurrent(), 0);
          return next;
        }
        clearInterval(stepIntervalRef.current);
        return prev;
      });
    }, perStepMs);

    return () => {
      clearInterval(stepIntervalRef.current);
      clearInterval(typingIntervalRef.current);
    };
  }, [mode, aiTyping, showTypewriter, processingSeconds, steps, stepIdx]);

  // Modo B - versão temporária com múltiplos <p>
  React.useEffect(() => {
    if (mode !== 'B' || !aiTyping || showTypewriter || !processingSeconds) {
      typingIntervalRefs.current.forEach(clearInterval);
      typingIntervalRefs.current = [];
      setStepTexts([]);
      return;
    }

    const totalMs = processingSeconds * 1000;
    const perStepMs = Math.max(200, Math.floor(totalMs / stepsNoCounters.length));

    const startTypingCurrent = (idx) => {
      clearInterval(typingIntervalRefs.current[idx]);
      const full = stepsNoCounters[idx];
      let i = 0;
      typingIntervalRefs.current[idx] = setInterval(() => {
        i += 1;
        setStepTexts(prev => {
          const newTexts = [...prev];
          newTexts[idx] = full.slice(0, i);
          return newTexts;
        });
        if (i >= full.length) {
          clearInterval(typingIntervalRefs.current[idx]);
        }
      }, 20);
    };

    startTypingCurrent(0);

    const stepInterval = setInterval(() => {
      setStepIdx(prev => {
        const next = prev + 1;
        if (next < stepsNoCounters.length) {
          setTimeout(() => startTypingCurrent(next), 0);
          return next;
        }
        clearInterval(stepInterval);
        return prev;
      });
    }, perStepMs);

    return () => {
      clearInterval(stepInterval);
      typingIntervalRefs.current.forEach(clearInterval);
      typingIntervalRefs.current = [];
    };
  }, [mode, aiTyping, showTypewriter, processingSeconds, stepsNoCounters]);

  return (
    <div className="message-log">

      {messages.map((msg, idx) =>
        msg.sender === 'user' ? (
          <UserMessage key={idx} text={msg.text} />
        ) : (
          <AiMessage key={idx} text={msg.text} typewriter={false} />
        )
      )}
      {aiTyping && !showTypewriter && (
        mode === 'B' ? (
          <div className="ai-message ai-loading modeB">
            <div className="ai-message-markdown">
              {stepTexts.map((text, i) => {
                const fullText = stepsNoCounters[i] || '';
                const isCurrentStep = i === stepIdx;
                const isTyped = i < stepIdx;
                const isTyping = isCurrentStep;
                return (
                  <p key={i} className={isTyping ? 'typing' : isTyped ? 'typed' : ''}>
                    {text || ''}
                  </p>
                );
              })}
            </div>
          </div>
        ) : (
          <div className="ai-message ai-loading">
            <p class="ai-message-markdown">
              <span class="loader"><ThreeDots visible /></span>
              {stepTextDisplayed || 'Gerando resposta'}
            </p>
          </div>
        )
      )}
      {aiTyping && showTypewriter && (
        <AiMessage text={pendingAiMsg} typewriter={true} onTypewriterEnd={onTypewriterEnd} />
      )}
      <div ref={messagesEndRef} />
    </div>
  );
}
