import React from 'react';
import './MessageLog.scss';
import { IconSparkles } from '@tabler/icons-react';
import { marked } from 'marked';
import { FourSquare } from 'react-loading-indicators';

export const UserMessage = ({ text }) => (
  <div className="user-message">
    <span>{text}</span>
  </div>
);

export const AiMessage = ({ text, typewriter, onTypewriterEnd }) => {
  const [displayed, setDisplayed] = React.useState(typewriter ? '' : text);
  React.useEffect(() => {
    if (typewriter) {
      setDisplayed('');
      let i = 0;
      const interval = setInterval(() => {
        setDisplayed(text.slice(0, i + 1));
        i++;
        if (i === text.length) {
          clearInterval(interval);
          if (onTypewriterEnd) onTypewriterEnd();
        }
      }, 25);
      return () => clearInterval(interval);
    }
  }, [text, typewriter, onTypewriterEnd]);

  return (
    <div className="ai-message">
      <span className="chat-ai-icon"><IconSparkles size={20} stroke={2} /></span>
      <p
        className="ai-message-markdown"
        dangerouslySetInnerHTML={{ __html: marked.parse(typewriter ? displayed : text) }}
      />
      {typewriter && displayed.length < text.length && (
        <span className="ai-typing-dots">
          <span>.</span><span>.</span><span>.</span>
        </span>
      )}
    </div>
  );
};

export default function MessageLog({ messages, aiTyping, showTypewriter, pendingAiMsg, onTypewriterEnd, messagesEndRef, mode = 'A', processingSeconds = null }) {
  const [stepIdx, setStepIdx] = React.useState(0);
  const [stepTextDisplayed, setStepTextDisplayed] = React.useState('');
  const stepIntervalRef = React.useRef();
  const typingIntervalRef = React.useRef();

  const steps = React.useMemo(() => ([
    'Gerando resposta',
    'Buscando base de dados (1/4)',
    'Identificando os pontos geográficos (2/4)',
    'Aplicando os filtros definidos (3/4)',
    'Aplicando camada no mapa (4/4)'
  ]), []);

  React.useEffect(() => {
    // Controle de etapas apenas no modo A, durante o estado de processamento (antes do typewriter)
    if (mode !== 'A' || !aiTyping || showTypewriter || !processingSeconds) {
      clearInterval(stepIntervalRef.current);
      clearInterval(typingIntervalRef.current);
      setStepIdx(0);
      setStepTextDisplayed('');
      return;
    }

    const totalMs = processingSeconds * 1000;
    const perStepMs = Math.max(200, Math.floor(totalMs / steps.length));

    // Função para iniciar tipagem da etapa atual
    const startTypingCurrent = () => {
      clearInterval(typingIntervalRef.current);
      setStepTextDisplayed('');
      const full = steps[stepIdx];
      let i = 0;
      typingIntervalRef.current = setInterval(() => {
        i += 1;
        setStepTextDisplayed(full.slice(0, i));
        if (i >= full.length) {
          clearInterval(typingIntervalRef.current);
        }
      }, 20);
    };

    // Inicia primeira etapa
    startTypingCurrent();

    // Avança etapas no intervalo calculado
    clearInterval(stepIntervalRef.current);
    stepIntervalRef.current = setInterval(() => {
      setStepIdx(prev => {
        const next = prev + 1;
        if (next < steps.length) {
          // Troca etapa e reinicia tipagem
          setStepTextDisplayed('');
          setTimeout(() => startTypingCurrent(), 0);
          return next;
        }
        clearInterval(stepIntervalRef.current);
        return prev;
      });
    }, perStepMs);

    return () => {
      clearInterval(stepIntervalRef.current);
      clearInterval(typingIntervalRef.current);
    };
  }, [mode, aiTyping, showTypewriter, processingSeconds, steps, stepIdx]);

  return (
    <div className="message-log">
      {messages.map((msg, idx) =>
        msg.sender === 'user' ? (
          <UserMessage key={idx} text={msg.text} />
        ) : (
          <AiMessage key={idx} text={msg.text} typewriter={false} />
        )
      )}
      {aiTyping && !showTypewriter && (
        mode === 'A' ? (
          <div className="ai-message">
            <span className="chat-ai-icon"><IconSparkles size={20} stroke={2} /></span>
            <span>{stepTextDisplayed || 'Gerando resposta'}</span>
            <span style={{ marginLeft: 8 }}>
              <FourSquare class="ai-loading" color="#888" size="small" />
            </span>
          </div>
        ) : (
          <div className="ai-message">
            <span className="chat-ai-icon"><IconSparkles size={20} stroke={2} /></span>
            <span>{`Gerando resposta (modo ${mode})`}</span>
          </div>
        )
      )}
      {aiTyping && showTypewriter && (
        <AiMessage text={pendingAiMsg} typewriter={true} onTypewriterEnd={onTypewriterEnd} />
      )}
      <div ref={messagesEndRef} />
    </div>
  );
}
