import React, { useState } from 'react';
import Chat from './components/Chat';
import './styles/App.scss';
import { IconArrowRight } from '@tabler/icons-react';

function App() {
  const [mode, setMode] = useState('A');
  const [processingSeconds, setProcessingSeconds] = useState(null);

  const cycleMode = () => {
    setMode(m => {
      if (m === 'A') return 'B';
      if (m === 'B') return 'C';
      return 'A';
    });
  };

  return <div className='main'>
    <div className='text-description'>
      <div>
        <h1>Chat response</h1>
        <h2>Feature para melhoria de percepção do usuário sobre tempo e racional do AI</h2>
        <div className='point'><IconArrowRight /><p>Tempo de resposta:{processingSeconds ? ` • ~${processingSeconds}s` : ''}</p></div>
        <div>
          <button onClick={cycleMode}>Alternar para {mode === 'A' ? 'Modo B' : mode === 'B' ? 'Modo C' : 'Modo A'}</button>
        </div>
      </div>
    </div>
    <div className='content'><Chat mode={mode} onProcessingStart={() => {}} onProcessingEnd={(secs) => setProcessingSeconds(secs)} /></div>
  </div>;
}

export default App;
