import React, { useState } from 'react';
import Chat from './components/Chat';
import './styles/App.scss';
import { IconArrowRight } from '@tabler/icons-react';

function App() {
  const [mode, setMode] = useState('A');
  const [processingSeconds, setProcessingSeconds] = useState(null);
  return <div className='main'>
    <div className='text-description'>
      <div>
        <h1>Chat response</h1>
        <h2>Feature para melhoria de percepção do usuário sobre tempo e racional do AI</h2>
        <div className='point'><IconArrowRight /><p>Modo atual: {mode === 'A' ? 'Modo A' : 'Modo B'}{processingSeconds ? ` • ~${processingSeconds}s` : ''}</p></div>
        <div>
          <button onClick={() => setMode(m => (m === 'A' ? 'B' : 'A'))}>Alternar para {mode === 'A' ? 'Modo B' : 'Modo A'}</button>
        </div>
      </div>
    </div>
    <div className='content'><Chat mode={mode} onProcessingStart={setProcessingSeconds} onProcessingEnd={() => setProcessingSeconds(null)} /></div>
  </div>;
}

export default App;
